CREATE TABLE "area_container" ("Id" INTEGER PRIMARY KEY  NOT NULL ,"X" INTEGER DEFAULT (null) ,"Y" INTEGER DEFAULT (null) ,"Z" INTEGER DEFAULT (null) ,"Type" VARCHAR,"Items" VARCHAR,"Fixed" INTEGER DEFAULT (1) , "Copper" INTEGER DEFAULT 0);
CREATE TABLE "area_creature" ("Id" integer PRIMARY KEY  DEFAULT (NULL) ,"AreaId" integer,"CreatureId" integer,"MobLevel" integer DEFAULT (NULL) ,"SpawnX" integer DEFAULT (NULL) ,"SpawnY" integer DEFAULT (NULL) ,"SpawnZ" INTEGER DEFAULT (0) , "Special" VARCHAR DEFAULT None, "AggroType" INTEGER DEFAULT 0, "Name" VARCHAR, "SpawnCriteria" INTEGER DEFAULT 0, "Equipment" VARCHAR DEFAULT None);
CREATE TABLE "area_effect" ("Id" INTEGER PRIMARY KEY  NOT NULL ,"AreaName" VARCHAR NOT NULL ,"Tint" INTEGER DEFAULT (0) ,"Fog" INTEGER DEFAULT (0) ,"FogColor" VARCHAR, "TintColor" VARCHAR, "Song" VARCHAR DEFAULT None, "Ambient" VARCHAR DEFAULT None, "AreaItems" VARCHAR DEFAULT None, "AreaCopper" INTEGER DEFAULT 0, "Particles" VARCHAR DEFAULT None, "GuardLevel" INTEGER DEFAULT 0);
CREATE TABLE "area_tile" ("Id" INTEGER PRIMARY KEY  NOT NULL ,"X" INTEGER,"Y" INTEGER,"Z" INTEGER,"Type" VARCHAR,"Name" VARCHAR,"Passable" INTEGER,"ObjectId" VARCHAR DEFAULT ('None') , "DoorId" INTEGER DEFAULT 0, "AreaEffectId" INTEGER DEFAULT 0);
CREATE TABLE "area_trap" ("Id" INTEGER PRIMARY KEY  NOT NULL ,"X" INTEGER,"Y" INTEGER,"Z" INTEGER, "TrapId" INTEGER DEFAULT 0);
CREATE TABLE "checkpoint" ("Id" INTEGER PRIMARY KEY  NOT NULL ,"X" INTEGER NOT NULL  DEFAULT (0) ,"Y" INTEGER NOT NULL  DEFAULT (0) ,"Z" INTEGER DEFAULT (0) , "NpcId" INTEGER DEFAULT 0);
CREATE TABLE "door" ("Id" integer PRIMARY KEY  DEFAULT (0) ,"GotoX" integer DEFAULT (0) ,"GotoY" INTEGER DEFAULT (0) ,"GotoZ" INTEGER DEFAULT (0) ,"Locked" INTEGER DEFAULT (0) , "CreatureIds" VARCHAR DEFAULT None, "Premium" INTEGER DEFAULT 0);
CREATE TABLE "editor_option" ("X" INTEGER, "Y" INTEGER, "Z" INTEGER);
CREATE TABLE "quest_category" ("Id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "Name" VARCHAR);
CREATE TABLE "trap" ("Id" INTEGER PRIMARY KEY  NOT NULL  UNIQUE , "Active" INTEGER DEFAULT 0, "Damage" INTEGER DEFAULT 0, "DamageType" VARCHAR DEFAULT None, "AbilityId" INTEGER DEFAULT 0, "Repeat" INTEGER DEFAULT 1, "Name" VARCHAR, "EffectSpan" INTEGER DEFAULT 1);
CREATE TABLE "trigger" ("Id" INTEGER PRIMARY KEY  NOT NULL  UNIQUE , "X" INTEGER DEFAULT 0, "Y" INTEGER DEFAULT 0, "Z" INTEGER DEFAULT 0, "TrapId" INTEGER DEFAULT 0, "DoorId" INTEGER DEFAULT 0, "ActiveTime" INTEGER DEFAULT 0);
CREATE TABLE quest (Id INTEGER PRIMARY KEY, Name VARCHAR, RewardMessage VARCHAR, QuestMessage VARCHAR, Level INTEGER, Type VARCHAR, TargetNumber INTEGER, TargetType VARCHAR, TargetId INTEGER, NpcId INTEGER, OrderNr INTEGER, RewardXp INTEGER, RewardItemId INTEGER, RewardCopper INTEGER, ParentQuestId INTEGER, Description TEXT, EventId INTEGER, NextQuestId INTEGER, ReturnForReward INTEGER, QuestItems VARCHAR, "RewardAbilityId" INTEGER DEFAULT 0, "LearnClassId" INTEGER DEFAULT 0, "QuestAbilityId" INTEGER DEFAULT 0, "CategoryId" INTEGER DEFAULT 0);
